Mmenu.addons.toggles=function(){var n=this;this.bind("initPanels:after",function(e){e.forEach(function(e){Mmenu.DOM.find(e,"input").forEach(function(e){Mmenu.refactorClass(e,n.conf.classNames.toggles.toggle,"mm-toggle"),Mmenu.refactorClass(e,n.conf.classNames.toggles.check,"mm-check")})}),e.forEach(function(e){Mmenu.DOM.find(e,"input.mm-toggle, input.mm-check").forEach(function(e){var n=e.closest("li"),t=e.id||Mmenu.getUniqueId();if(!Mmenu.DOM.children(n,'label[for="'+t+'"]').length){e.id=t,n.prepend(e);var c=Mmenu.DOM.create("label.mm-"+(e.matches(".mm-toggle")?"toggle":"check"));c.setAttribute("for",t);var o=Mmenu.DOM.children(n,".mm-listitem__text")[0];o.parentElement.insertBefore(c,o.nextSibling)}})})})},Mmenu.configs.classNames.toggles={toggle:"Toggle",check:"Check"};