/**	Options for the dividers add-on. */
interface mmOptionsDividers {
	/** Whether or not to automatically add dividers to all listviews. */
	add			: boolean

	/** Where to add dividers. */
	addTo		: string

	/** Whether or not dividers should be fixed / sticky */
	fixed		: boolean

	/** The type of dividers. */
	type		: string
}
